<script>
export default {
  name: "main-header",

  // tilt animation
  mounted() {
    const tiltContainer = document.querySelector(".tilt-anim-wrap");
    const decorBlock = document.querySelector(".decor-block");
    const menuSpan = document.querySelector(".menu-word-span");

    tiltContainer.addEventListener("mousemove", (e) => {
      let xAxis = (window.innerWidth - e.pageX) / 7 - 15;
      let yAxis = (window.innerWidth - e.pageY * 20) / 94;
      decorBlock.style.transform = `rotateY(${xAxis}deg) rotateX(${yAxis}deg)`;
    });
    tiltContainer.addEventListener("mouseenter", () => {
      decorBlock.style.transition = "none";
      menuSpan.style.transform = `translateZ(-30px) scale(1.05)`;
    });

    tiltContainer.addEventListener("mouseleave", () => {
      decorBlock.style.transition = "all 2s ease";
      decorBlock.style.transform = `rotateY(0deg) rotateX(0deg)`;
      menuSpan.style.transform = `translateZ(0)`;
    });
  },
};
</script>

<style lang="scss" scoped>
* {
  cursor: none;
}
.menu-container {
  @include position(space-between, center, "");
  padding: 20px;
  .logo {
    display: inline-block;
    padding: 30px;
  }
  .decor-block {
    transform-style: preserve-3d;
    background-color: rgba(0, 0, 0, 0.14);
    width: 130px;
    height: 130px;
    @include position(center, center, "");
  }
  .tilt-anim-wrap {
    box-sizing: border-box;
    padding: 40px;
  }
}
.main-header-nav {
  position: relative;
  .main-list-link {
    padding: 20px 0;
    &:hover .menu-word-span {
      background-color: #9fbcff;
      // background-color: rgba(30, 46, 63, 0.459);
      // color: #fff;
    }
  }
  .menu-word-span {
    display: inline-block;
    padding: 20px;
    transition: $buttonTransition;
    text-decoration: none;
    @include font("Jost", 1.2em, 400, rgb(0, 0, 0));
  }
}
</style>